sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here
	//Construct a GlideRecord query to find all NeedIt Task records with Due date field values in the past for the same Assigned to.
	//The records cannot be in the Closed state.
	var today = new GlideDateTime();
	var alsoDue = new GlideRecord('x_58872_needit_needit_task');
	alsoDue.addQuery('state', '<', 3);
	alsoDue.addQuery('due_date', '<=', today);
	alsoDue.addQuery('assigned_to', current.assigned_to);

	alsoDue.query();

	//Print to the message field on a notification if the user has more than one overdue assigned NeedIt Task
	if(alsoDue.getRowCount() > 1){
		template.print("In addition to this overdue NeedIt Task, you have " + (alsoDue.getRowCount() -1) + " more overdue NeedIt Tasks.")
	}

})(current, template, email, email_action, event);
